Module.Content:

#requireResource("com.atlassian.jira.gadgets:common")
#includeResources()

<script type="text/javascript">
    (function () {
        gadgets.window.setTitle("__MSG_module.gadget.title__");
        var gadget = AJS.Gadget({
            baseUrl: "__ATLASSIAN_BASE_URL__",
            useOauth: "/rest/gadget/1.0/currentUser",
            view: {
                enableReload: false,
                onResizeReload: false,
                template: function (args) {
                    var gadget = this;

                    if (AJS.$("#content", gadget.getView()).length === 0) {
                        gadget.getView().append(AJS.$("#content"));
                    }
                    gadget.getView().addClass("reference-gadget");
                    AJS.$("#content").html("<div>__MSG_module.gadget.resourcecontents__</div><div id='reference-response'>" + JSON.stringify(args) + "</div>");
                },
                args: [
                    {
                        ajaxOptions: function () {
                            return {
                                url: "/rest/reference-plugin/1.0/endpoint"
                            };
                        }
                    }
                ]
            }
        });
    })();
</script>

<div id="content">

</div>
